<template>
  <div class="topButton" v-title="100" @click="handleTop"></div>
  <!-- 一键回到顶部 -->
</template>

<script>
import Vue from "vue"
Vue.directive("title",{
    //插入到dom之后的钩子函数
    inserted(el,binding){
        el.style.display = "none"
        window.onscroll = ()=>{
            if((document.body.scrollTop || document.documentElement.scrollTop)>binding.value){
                el.style.display = "block"
            }else{
                el.style.display = "none"
            }
        }
    },
    //指令的解绑的钩子函数
    unbind(){
        window.onscroll = null
    }
})
export default {
    methods: {
        handleTop(){
            document.documentElement.scrollTop=0
        },
    },
}
</script>

<style>
    .topButton{
            background: url(data:image/png;base64,) bottom no-repeat transparent;
            background-size: 40px 40px;
            position: fixed;
            bottom: 50px;
            right: 0;
            z-index: 101;
            width: 64px;
            height: 64px;
        }
</style>